import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import '../styles/Sidebar.module.css';

const Sidebar = () => {
  const { user } = useAuth();
  const location = useLocation();

  const isActive = (path) => {
    return location.pathname === path;
  };

  const getRoleLinks = () => {
    const baseLinks = [
      { path: '/cattle', label: 'üêÑ Cattle', icon: 'üêÑ' },
    ];

    switch (user?.role) {
      case 'Farm Manager':
        return [
          ...baseLinks,
          { path: '/tasks', label: 'üìã Tasks', icon: 'üìã' },
          { path: '/reports', label: 'üìä Reports', icon: 'üìä' },
          { path: '/analytics', label: 'üìà Analytics', icon: 'üìà' },
          { path: '/qr-generator', label: 'üì± QR Generator', icon: 'üì±' },
        ];
      
      case 'Veterinarian':
        return [
          ...baseLinks,
          { path: '/health-records', label: 'üè• Health Records', icon: 'üè•' },
          { path: '/health-alerts', label: '‚ö†Ô∏è Health Alerts', icon: '‚ö†Ô∏è' },
          { path: '/qr-scanner', label: 'üì± QR Scanner', icon: 'üì±' },
        ];
      
      case 'Worker':
        return [
          ...baseLinks,
          { path: '/tasks', label: 'üìã Tasks', icon: 'üìã' },
          { path: '/tasks/checklist', label: '‚úÖ Checklist', icon: '‚úÖ' },
          { path: '/qr-scanner', label: 'üì± QR Scanner', icon: 'üì±' },
        ];
      
      case 'Admin':
        return [
          ...baseLinks,
          { path: '/users', label: 'üë• Users', icon: 'üë•' },
          { path: '/logs', label: 'üìù Logs', icon: 'üìù' },
          { path: '/settings', label: '‚öôÔ∏è Settings', icon: '‚öôÔ∏è' },
          { path: '/reports', label: 'üìä Reports', icon: 'üìä' },
          { path: '/analytics', label: 'üìà Analytics', icon: 'üìà' },
          { path: '/qr-generator', label: 'üì± QR Generator', icon: 'üì±' },
        ];
      
      default:
        return baseLinks;
    }
  };

  const getDashboardLink = () => {
    switch (user?.role) {
      case 'Farm Manager':
        return '/dashboard/manager';
      case 'Veterinarian':
        return '/dashboard/vet';
      case 'Worker':
        return '/dashboard/worker';
      case 'Admin':
        return '/dashboard/admin';
      default:
        return '/dashboard/manager';
    }
  };

  return (
    <aside className="sidebar">
      <div className="sidebar-header">
        <h3>Navigation</h3>
      </div>
      
      <nav className="sidebar-nav">
        <Link 
          to={getDashboardLink()} 
          className={`sidebar-link ${isActive(getDashboardLink()) ? 'active' : ''}`}
        >
          üè† Dashboard
        </Link>
        
        {getRoleLinks().map((link) => (
          <Link
            key={link.path}
            to={link.path}
            className={`sidebar-link ${isActive(link.path) ? 'active' : ''}`}
          >
            {link.icon} {link.label}
          </Link>
        ))}
      </nav>
    </aside>
  );
};

export default Sidebar;
