import React from 'react';
import { Link } from 'react-router-dom';
import '../../styles/Dashboard.module.css';

const WorkerDashboard = () => {
  return (
    <div className="dashboard-container">
      <h2>Worker Dashboard</h2>
      <div className="dashboard-cards">
        <div className="dashboard-card">
          <h3>Tasks</h3>
          <p>View and update assigned tasks.</p>
          <Link to="/tasks" className="btn btn-primary">View Tasks</Link>
        </div>
        <div className="dashboard-card">
          <h3>Checklist</h3>
          <p>Update completed tasks in checklist.</p>
          <Link to="/tasks/checklist" className="btn btn-primary">Checklist</Link>
        </div>
        <div className="dashboard-card">
          <h3>Cattle</h3>
          <p>View all cattle assigned to you.</p>
          <Link to="/cattle" className="btn btn-primary">View Cattle</Link>
        </div>
        <div className="dashboard-card">
          <h3>QR Scanner</h3>
          <p>Scan QR codes for cattle profiles.</p>
          <Link to="/qr-scanner" className="btn btn-primary">QR Scanner</Link>
        </div>
      </div>
    </div>
  );
};

export default WorkerDashboard;
