import React, { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import '../styles/Navbar.module.css';

const Navbar = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [showDropdown, setShowDropdown] = useState(false);
  const [showMobileMenu, setShowMobileMenu] = useState(false);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const isActive = (path) => {
    return location.pathname === path;
  };

  const getDashboardLink = () => {
    switch (user?.role) {
      case 'Farm Manager':
        return '/dashboard/manager';
      case 'Veterinarian':
        return '/dashboard/vet';
      case 'Worker':
        return '/dashboard/worker';
      case 'Admin':
        return '/dashboard/admin';
      default:
        return '/dashboard/manager';
    }
  };

  const getRoleLinks = () => {
    const baseLinks = [
      { path: '/cattle', label: 'üêÑ Cattle', icon: 'üêÑ' },
    ];

    switch (user?.role) {
      case 'Farm Manager':
        return [
          ...baseLinks,
          { path: '/tasks', label: 'üìã Tasks', icon: 'üìã' },
          { path: '/reports', label: 'üìä Reports', icon: 'üìä' },
          { path: '/analytics', label: 'üìà Analytics', icon: 'üìà' },
          { path: '/qr-generator', label: 'üì± QR Generator', icon: 'üì±' },
        ];
      
      case 'Veterinarian':
        return [
          ...baseLinks,
          { path: '/health-records', label: 'üè• Health Records', icon: 'üè•' },
          { path: '/health-alerts', label: '‚ö†Ô∏è Health Alerts', icon: '‚ö†Ô∏è' },
          { path: '/qr-scanner', label: 'üì± QR Scanner', icon: 'üì±' },
        ];
      
      case 'Worker':
        return [
          ...baseLinks,
          { path: '/tasks', label: 'üìã Tasks', icon: 'üìã' },
          { path: '/tasks/checklist', label: '‚úÖ Checklist', icon: '‚úÖ' },
          { path: '/qr-scanner', label: 'üì± QR Scanner', icon: 'üì±' },
        ];
      
      case 'Admin':
        return [
          ...baseLinks,
          { path: '/users', label: 'üë• Users', icon: 'üë•' },
          { path: '/logs', label: 'üìù Logs', icon: 'üìù' },
          { path: '/settings', label: '‚öôÔ∏è Settings', icon: '‚öôÔ∏è' },
          { path: '/reports', label: 'üìä Reports', icon: 'üìä' },
          { path: '/analytics', label: 'üìà Analytics', icon: 'üìà' },
          { path: '/qr-generator', label: 'üì± QR Generator', icon: 'üì±' },
        ];
      
      default:
        return baseLinks;
    }
  };

  return (
    <nav className="navbar">
      <div className="navbar-brand">
        <Link to={getDashboardLink()} className="navbar-logo">
          üêÑ CowCo
        </Link>
      </div>

      {/* Desktop Navigation */}
      <div className="navbar-nav desktop-nav">
        <Link 
          to={getDashboardLink()} 
          className={`nav-link ${isActive(getDashboardLink()) ? 'active' : ''}`}
        >
          üè† Dashboard
        </Link>
        
        {getRoleLinks().map((link) => (
          <Link
            key={link.path}
            to={link.path}
            className={`nav-link ${isActive(link.path) ? 'active' : ''}`}
          >
            {link.icon} {link.label}
          </Link>
        ))}
      </div>

      {/* Mobile Menu Toggle */}
      <button 
        className="navbar-toggle"
        onClick={() => setShowMobileMenu(!showMobileMenu)}
      >
        ‚ò∞
      </button>

      {/* Mobile Navigation */}
      <div className={`navbar-nav mobile-nav ${showMobileMenu ? 'show' : ''}`}>
        <Link 
          to={getDashboardLink()} 
          className={`nav-link ${isActive(getDashboardLink()) ? 'active' : ''}`}
          onClick={() => setShowMobileMenu(false)}
        >
          üè† Dashboard
        </Link>
        
        {getRoleLinks().map((link) => (
          <Link
            key={link.path}
            to={link.path}
            className={`nav-link ${isActive(link.path) ? 'active' : ''}`}
            onClick={() => setShowMobileMenu(false)}
          >
            {link.icon} {link.label}
          </Link>
        ))}
      </div>

      {/* User Menu */}
      <div className="navbar-menu">
        <div className="navbar-end">
          <div className="navbar-item">
            <div className="user-dropdown">
              <button 
                className="user-button"
                onClick={() => setShowDropdown(!showDropdown)}
              >
                <span className="user-avatar">üë§</span>
                <span className="user-name">{user?.name}</span>
                <span className="user-role">({user?.role})</span>
              </button>
              
              {showDropdown && (
                <div className="dropdown-menu">
                  <div className="dropdown-item">
                    <strong>{user?.name}</strong>
                    <small>{user?.email}</small>
                  </div>
                  <hr />
                  <button className="dropdown-item" onClick={handleLogout}>
                    Logout
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
