import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/Cattle.module.css';

const CattleCard = ({ cattle }) => {
  const getHealthStatus = (health) => {
    switch (health) {
      case 'Excellent':
        return { color: 'green', icon: 'ðŸŸ¢' };
      case 'Good':
        return { color: 'blue', icon: 'ðŸ”µ' };
      case 'Fair':
        return { color: 'orange', icon: 'ðŸŸ¡' };
      case 'Poor':
        return { color: 'red', icon: 'ðŸ”´' };
      default:
        return { color: 'gray', icon: 'âšª' };
    }
  };

  const healthStatus = getHealthStatus(cattle.health);

  return (
    <div className="cattle-card">
      <div className="cattle-header">
        <h3 className="cattle-name">{cattle.name}</h3>
        <span className={`health-status ${healthStatus.color}`}>
          {healthStatus.icon} {cattle.health}
        </span>
      </div>
      
      <div className="cattle-info">
        <div className="info-row">
          <span className="label">ID:</span>
          <span className="value">{cattle.id}</span>
        </div>
        <div className="info-row">
          <span className="label">Breed:</span>
          <span className="value">{cattle.breed}</span>
        </div>
        <div className="info-row">
          <span className="label">Age:</span>
          <span className="value">{cattle.age} years</span>
        </div>
        <div className="info-row">
          <span className="label">Weight:</span>
          <span className="value">{cattle.weight} kg</span>
        </div>
        <div className="info-row">
          <span className="label">Location:</span>
          <span className="value">{cattle.location}</span>
        </div>
      </div>
      
      <div className="cattle-actions">
        <Link to={`/cattle/${cattle.id}`} className="btn btn-primary">
          View Profile
        </Link>
        <Link to={`/cattle/${cattle.id}/qr`} className="btn btn-secondary">
          QR Code
        </Link>
      </div>
    </div>
  );
};

export default CattleCard;
